import { useState, useEffect } from 'react';
import { Badge } from '../Badge/Badge';
import { TabIcons } from '../TabIcons/TabIcons';
import { HexagonScore } from '../HexagonScore/HexagonScore';
import { ResourceCard } from '../ResourceCard/ResourceCard';
import styles from './MasteryDashboard.module.css';

// Minimum number of answers needed to show mastery score
const MIN_ANSWERS = 25;

interface UserProgress {
  responseCount: number;
  masteryScore: number | null;
}

export const MasteryDashboard = () => {
  const [userProgress, setUserProgress] = useState<UserProgress | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  
  useEffect(() => {
    // Fetch user progress from backend
    const fetchUserProgress = async () => {
      try {
        const response = await fetch('/api/user/progress', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        
        if (response.ok) {
          const data = await response.json();
          setUserProgress(data);
        } else {
          console.error('Failed to fetch user progress');
        }
      } catch (error) {
        console.error('Error fetching user progress:', error);
      } finally {
        setLoading(false);
      }
    };
    
    fetchUserProgress();
  }, []);

  const resources = [
    { title: 'View Formula Sheet', icon: 'formula', url: 'https://files.coachingactuaries.com/ca/library/astam%20formula%20sheet.pdf', external: true },
    { title: 'Guide to Written-Answer Questions', icon: 'guide', url: '/astam/learn/5063' },
    { title: 'Knowing You\'re Ready', icon: 'ready' }
  ];

  const practiceOptions = [
    'Build a Custom Quiz',
    'Build a Custom Exam',
    'Take a Mock Exam'
  ];

  const isMasteryUnlocked = userProgress && userProgress.responseCount >= MIN_ANSWERS;

  return (
    <div className={styles.dashboardContainer}>
      <div className={styles.dashboardCard}>
        {/* Header Section */}
        <div className={styles.dashboardHeader}>
          <div className={styles.headerTop}>
            <div className={styles.titleIcon}>
              <svg className={styles.headerIcon} viewBox="0 0 24 24" fill="currentColor">
                <path d="M17 3H7c-1.1 0-1.99.9-1.99 2L5 21l7-3 7 3V5c0-1.1-.9-2-2-2z"/>
              </svg>
              Practice Mastery
            </div>
            <div className={styles.masteryLabel}>
              Mastery Score <Badge locked={!isMasteryUnlocked} />
            </div>
          </div>
          <TabIcons />
        </div>

        {/* Main Content with 2-column layout */}
        <div className={styles.dashboardMain}>
          {/* Left Column */}
          <div className={styles.leftPanel}>
            <div className={styles.introBox}>
              {loading ? (
                <h2>Loading your progress...</h2>
              ) : !isMasteryUnlocked ? (
                <>
                  <h2>Answer {MIN_ANSWERS - (userProgress?.responseCount || 0)} more questions to unlock your Mastery Score</h2>
                  <p>
                    To get started, take a practice quiz or exam. Based on your performance, 
                    we'll show you a personalized mastery score and track your progress.
                  </p>
                  {userProgress && (
                    <div className={styles.progressWrapper}>
                      <progress 
                        value={userProgress.responseCount} 
                        max={MIN_ANSWERS} 
                        className={styles.progressBar}
                      />
                      <p className={styles.progressText}>
                        {userProgress.responseCount}/{MIN_ANSWERS} questions answered
                      </p>
                    </div>
                  )}
                </>
              ) : (
                <>
                  <h2>Your Mastery Score: {userProgress?.masteryScore?.toFixed(1)}</h2>
                  <p>
                    Continue practicing to improve your mastery score. We'll adjust your 
                    score based on the difficulty of questions you answer correctly.
                  </p>
                </>
              )}
              <button className={styles.primaryButton}>
                <svg className={styles.buttonIcon} viewBox="0 0 24 24" fill="currentColor">
                  <path d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z"/>
                </svg>
                Build a Custom Quiz
              </button>
            </div>

            <div className={styles.morePractice}>
              <h3>More Ways to Practice</h3>
              <div className={styles.practiceOptions}>
                {practiceOptions.map((option, index) => (
                  <div key={index} className={styles.practiceItem}>
                    <button className={styles.practiceButton}>
                      {option}
                    </button>
                    <button className={styles.infoButton} title={`Learn about ${option}`}>
                      <svg width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M11.18 12.69c0 .3.07.53.21.7v.01c.08.1.19.17.32.21.08.03.17.04.27.04.2 0 .38-.06.53-.18.16-.12.25-.29.29-.5.03-.13.09-.26.17-.39.05-.07.1-.14.16-.2.1-.12.24-.26.41-.4.1-.09.21-.19.33-.28.18-.16.35-.31.49-.44.13-.12.24-.23.34-.33.22-.22.4-.48.55-.78.15-.3.23-.63.23-.99 0-.49-.14-.92-.42-1.31-.28-.39-.67-.69-1.17-.91-.5-.22-1.07-.33-1.7-.33-.57 0-1.13.09-1.69.27-.56.18-1.06.43-1.5.75-.17.13-.29.26-.36.4-.07.13-.11.31-.11.51 0 .3.09.56.26.77.17.21.38.31.62.31.2 0 .47-.09.8-.28l.35-.17c.27-.16.52-.28.74-.36.22-.08.45-.12.67-.12.18 0 .34.03.47.09.06.03.12.06.17.1.15.13.23.3.23.51 0 .21-.06.4-.17.58-.11.18-.28.39-.5.63-.31.32-.55.63-.73.94-.17.31-.26.69-.26 1.15z" fill="currentColor"/>
                        <path d="M10.61 15.71c0 .4.13.74.4 1.01.27.27.59.41 1 .41.41 0 .75-.14 1.02-.41.27-.27.4-.61.4-1.01 0-.4-.13-.74-.4-1.01-.27-.27-.63-.41-1.02-.41-.4 0-.73.14-1 .41-.27.27-.4.61-.4 1.01z" fill="currentColor"/>
                        <path fill-rule="evenodd" clip-rule="evenodd" d="M1 12C1 5.92 5.92 1 12 1c6.08 0 11 4.92 11 11 0 6.08-4.92 11-11 11-6.08 0-11-4.92-11-11zm20 0c0-4.96-4.04-9-9-9s-9 4.04-9 9c0 4.96 4.04 9 9 9s9-4.04 9-9z" fill="currentColor"/>
                      </svg>
                    </button>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Right Column */}
          <div className={styles.rightPanel}>
            <div className={styles.masteryScoreBox}>
              <h3>Mastery Score</h3>
              <HexagonScore 
                score={userProgress?.responseCount || 0} 
                maxScore={MIN_ANSWERS} 
                locked={!isMasteryUnlocked} 
                actualScore={userProgress?.masteryScore}
              />
              <div className={styles.masteryInfo}>
                <h4>
                  <strong>{userProgress?.responseCount || 0}</strong>/{MIN_ANSWERS}
                </h4>
                <p>Unlock Mastery Score by<br/>Answering {MIN_ANSWERS} Questions</p>
              </div>
            </div>
            
            <div className={styles.resourceLinks}>
              <h3>Additional Resources</h3>
              <div className={styles.resourceCards}>
                {resources.map((resource, index) => (
                  <ResourceCard
                    key={index}
                    title={resource.title}
                    icon={resource.icon}
                    url={resource.url}
                    external={resource.external}
                  />
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
