import { useState, FormEvent } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { CloudDivider } from '../CloudDivider/CloudDivider';
import styles from './LoginForm.module.css';

export const LoginForm = () => {
  const { login, isLoading, error } = useAuth();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    try {
      await login(email, password);
    } catch (err) {
      // Error is handled by AuthContext
    }
  };

  return (
    <div className={styles.loginPage}>
      <div className={styles.graphicPanel}>        <div className={styles.brandContent}>
          <img src="/brand/logo.svg" alt="Kubic IQ Logo" className={styles.logo} />
          <h1>Welcome to KubicIQ</h1>
          <p>Master the SAT with ease & personalized tutoring</p>
        </div>
        <CloudDivider />
      </div>
      
      <div className={styles.formPanel}>
        <div className={styles.formContainer}>
          <form onSubmit={handleSubmit} className={styles.form}>
            <h2>Sign In</h2>
            
            <p className={styles.info}>
              Access your personalized SAT prep experience
            </p>

            {error && (
              <div className={styles.error}>{error}</div>
            )}

            <div className={styles.inputGroup}>
              <label htmlFor="email">Email</label>
              <input
                id="email"
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                className={styles.input}
              />
            </div>

            <div className={styles.inputGroup}>
              <label htmlFor="password">Password</label>
              <input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                className={styles.input}
              />
            </div>

            <button
              type="submit"
              disabled={isLoading}
              className={styles.submitButton}
            >
              {isLoading ? 'Signing in...' : 'Sign In'}
            </button>

            <div className={styles.links}>
              <a href="/forgot-password" className={styles.link}>
                Forgot password?
              </a>
              <a href="/signup" className={styles.link}>
                Create account
              </a>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};
